def largestSumOfAverages(A, K):
    A = list(A)  # Convert set to list
    n = len(A)
    pre_sum = [0.0] * (n + 1)
    for i in range(n):
        pre_sum[i + 1] = pre_sum[i] + A[i]
    dp = [0.0] * n
    for i in range(n):
        dp[i] = (pre_sum[n] - pre_sum[i]) / (n - i)
