def noAdjacentDup(s):
    n = len(s)
    s = list(s)
    i = 1
    while i < n:
        if s[i] == s[i - 1]:
            s[i] = 'a'
            while s[i] == s[i - 1] or (i + 1 < n and s[i] == s[i + 1]):
                s[i] = chr(ord(s[i]) + 1)
            i += 1
        i += 1
    return ''.join(s)
