def largestSumOfAverages(A, K):
    n = len(A)
    pre_sum = [0.0] * (n + 1)
    for i in range(n):
        pre_sum[i + 1] = pre_sum[i] + A[i]
    dp = [[0.0] * n for _ in range(K)]
    for i in range(n):
        dp[0][i] = (pre_sum[n] - pre_sum[i]) / (n - i)
    for k in range(1, K):
        for i in range(n):
            for j in range(i + 1, n):
                dp[k][i] = max(dp[k][i], (pre_sum[j] - pre_sum[i]) / (j - i) + dp[k - 1][j])
    return dp[K - 1][0]
